import React, { useState } from 'react';
import { GifGrid } from '../GifGrid/GifGrid';
import { InputUI } from '../InputUI/InputUI';

export const GifExpertApp = () => {

  const [categories, setCategories] = useState(['Hola']);

  const handleLook = (event) => {    
    if (event.keyCode == 13) {
      console.log(event.keyCode);
      setCategories([...categories,event.target.value])      
    }
  }

  return (
    <>
      <h2>Gif Expert App</h2>
      <hr />
      <InputUI
        holder="Look some Character"
        event = {handleLook}
      />      
      {
        categories.map( (category,idx) => <GifGrid key={category+idx} category= {category}  />)
      }      
    </>
  )
}
